package com.yxtar.app.base.widget;import com.google.gwt.user.client.ui.HasAlignment;import com.google.gwt.user.client.ui.Label;import com.google.gwt.user.client.ui.PopupPanel;import com.google.gwt.user.client.ui.VerticalPanel;public class BaseLoadingWidget implements LoadingPanel{  private VerticalPanel verticalPanel = new VerticalPanel();  private PopupPanel dialogBox = new PopupPanel(true, true);  private PopupPanel glassBox = new PopupPanel(true, true);  private Label note = new Label();  public BaseLoadingWidget(String msg) {    this();    setMsg(msg);  }  public BaseLoadingWidget() {    verticalPanel.setCellHorizontalAlignment(note, HasAlignment.ALIGN_CENTER);    verticalPanel.setCellVerticalAlignment(note, HasAlignment.ALIGN_MIDDLE);    verticalPanel.add(note);    dialogBox.add(verticalPanel);  }  public void setMsg(String msg) {    note.setText(msg);  }  public void show() {    dialogBox.show();    glassBox.show();    dialogBox.center();  }  public void hiden() {    dialogBox.hide();    glassBox.hide();  }}