package com.yxtar.app.base.widget;import com.google.gwt.dom.client.Style.Position;import com.google.gwt.dom.client.Style.Unit;import com.google.gwt.user.client.ui.FlowPanel;import com.google.gwt.user.client.ui.PopupPanel;import com.yxtar.app.base.widget.CalendarWidget.CalendarEventHandler;import com.yxtar.app.base.widget.DateBoxWidget.HolderCalendar;/** * dialog panel with date-gird * @author wufeng *  */public class DateSearch implements HolderCalendar {	private FixPopupPanel dialogBox = new FixPopupPanel(false,true);	private FixPopupPanel glassBox = new FixPopupPanel(true,true);	private FlowPanel root= new FlowPanel();	private CalendarWidget calendar = new CalendarWidget();	public DateSearch() {		dialogBox.add(root);		root.add(calendar);		root.getElement().setAttribute("style", "margin: 0px auto;position: relative;left: 0px;");		root.setStyleName("order_search_calendar_pop");	}	/**	 * display this panel in center of screen	 */	public void show() {		glassBox.shows();		dialogBox.shows();	}	/**	 * display this panel in center of screen	 */	public void hide() {		glassBox.hides();		dialogBox.hides();	}	public CalendarWidget getCalendarWidget(){		return this.calendar;	}	@Override	public void setHandler(CalendarEventHandler handler) {		calendar.clearHandler();		calendar.addCalendarEventHandler(handler);	}}