package com.yxtar.app.base.widget;import com.google.gwt.event.shared.GwtEvent;import com.yxtar.app.base.widget.StepPanel.Step;public class StepMoveEndEvent extends GwtEvent<StepMoveEndEventHandler> {  public int getDirection() {    return direction;  }  public void setDirection(int direction) {    this.direction = direction;  }  public static Type<StepMoveEndEventHandler> TYPE = new Type<StepMoveEndEventHandler>();  Step targetStep = null;    public int getStepIndex() {    return stepIndex;  }  public void setStepIndex(int stepIndex) {    this.stepIndex = stepIndex;  }  int stepIndex = 0;  int direction=0;  public Step getTargetStep() {    return targetStep;  }  public void setTargetStep(Step targetStep) {    this.targetStep = targetStep;  }  public StepMoveEndEvent(Step step,int index,int dir) {    targetStep = step;    stepIndex=index;  }  @Override  public com.google.gwt.event.shared.GwtEvent.Type<StepMoveEndEventHandler> getAssociatedType() {    return TYPE;  }  @Override  protected void dispatch(StepMoveEndEventHandler handler) {    handler.onStepMoveEnd(this);  }}