package com.yxtar.app.base.widget;import com.google.gwt.event.dom.client.ClickHandler;import com.google.gwt.user.client.ui.Button;import com.google.gwt.user.client.ui.HorizontalPanel;/** *  * @author wufeng * */public class WTxtBoxWithButton extends WTxtBox {  protected HorizontalPanel horizontalPanel = new HorizontalPanel();  private Button button = new Button();  public WTxtBoxWithButton() {    super();    initWidget(horizontalPanel);    horizontalPanel.add(containerFlowPanel);    horizontalPanel.add(button);    button.setStylePrimaryName("input-button-default");  }  public WTxtBoxWithButton(String width, Integer height) {    super(width, height);    initWidget(horizontalPanel);    horizontalPanel.setWidth("80%");    horizontalPanel.add(containerFlowPanel);    horizontalPanel.add(button);    button.setStylePrimaryName("input-button-default");  }    public void setBtnWidth(String width){	horizontalPanel.setWidth(width);  }  /**   *    */  @Override  public final void setProtected(boolean flag) {    button.setVisible(!flag);    super.setProtected(flag);  }  public final void setButtonStyle(String style) {    button.addStyleName(style);  }  public final void addButtonStyle(String style) {	  button.addStyleName(style);  }      public final void addClick(ClickHandler clickHandler) {    button.addClickHandler(clickHandler);  }    public final void setInputStyle(String style){	  super.addInputBoxStyle(style);  }    public void readOnly(){	super.readOnly();  }}